import ApolloClient from 'apollo-boost';
import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import gql from 'graphql-tag';
import {Query} from 'react-apollo';

// initialize a GraphQL client
const client = new ApolloClient({
  uri: 'https://countries.trevorblades.com'
});

// write a GraphQL query that asks for names and codes for all countries
const GET_COUNTRIES = gql`
 {
    countries{
    name
    native
    emoji
    currency
    languages {
      code
      name
    }
  }
}
`;

// create a component that renders an API data-powered select input
class countryDetail extends Component {
  state = {
    country: 'US'
  };

  // set the selected country to the new input value
  onCountryChange = event => {
    this.setState({country: event.target.value});
  };

  render() {
    return (
      <Query query={GET_COUNTRIES} client={client}>
        {({loading, error, data}) => {
          if (loading) return <p>Loading...</p>;
          if (error) return <p>{error.message}</p>;
          return (
              <div>
            <select value={this.state.country} onChange={this.onCountryChange} style={{ marginTop: "30px", height: "50px", fontSize: "20px" }}>
              {data.countries.map(country => (
                  <div>
                  console.log(data),
                <option key={country.code} value={country.code}>
                  {country.name} {country.native} {country.emoji}
                </option>
                Country Selected: {country.emoji}
                </div>

              ))}
            </select>
            {/* <h3>Country Selected: {country.emoji}</h3> */}
            </div>
          );
        }}
      </Query>
    );
  }
}

export default countryDetail;
