import ApolloClient from 'apollo-boost';
import React, {Component} from 'react';
import gql from 'graphql-tag';
import {Query} from 'react-apollo';


const client = new ApolloClient({
	uri: 'https://countries.trevorblades.com'
  });

  const GET_COUNTRIES = gql`
  {
	countries{
	  name
	  native
	  emoji
	  currency
	  languages {
		code
		name
	  }
	}
  }
  `;
class CountrySelect extends Component {
	state = {
	  country: '',
	  currency:''
	};


	// set the selected country to the new input value
	onCountryChange = event => {
		this.setState({ country: event.target.value })
	}

	render() {
		return (
			<Query query={GET_COUNTRIES} client = {client}>
				{({ loading, error, data }) => {
					if (loading) return <p>Loading...</p>
					if (error) return <p>{error.message}</p>
					return (
						<div>
							<select
								value={this.state.country}
								onChange={this.onCountryChange}
								style={{ marginTop: "30px", height: "50px", fontSize: "20px" }}
							>
								{data.countries.map(({ code, name, emoji,native,currency }) => (
									<option key={code} value={code}>
										{emoji} {name} {native} {currency}
									</option>
								))}
							</select>
							<h3>Country Selected: {this.state.country} Currency:{currency}</h3>
						</div>
					)
				}}
			</Query>
		)
	}
}

export default CountrySelect
